(170, ' <span class=\"badge\">URGENT</span> Receptionist', '<ul>\n<li>\nGraduate of either of the following courses:\n\n<ul>\n<li><strong>Tourism, BS HRM and other related courses</strong></li>\n<li><strong>Computer Literate</strong></li>\n<li><strong>Has fast typing skills</strong></li>\n<li><strong>Can multitask and has good interpersonal skills</strong></li>\n\n</ul>\n\n\n\n</li>\n\n</ul>', 'cduh', '2017-03-29', 'close', 'non'),
(171, 'Pharmacist Assistant', '<ul>\r\n    <li>  	Graduate of Bachelor of Science in Pharmacy.</li>\r\n    <li> 	 	Organize, keen to detail, results-oriented and analytical</li>\r\n    <li>   	Physically and mentally fit and able to accomplish the demands of the position.</li>\r\n    <li>  	 	Male/Female, 21 - 35 years old</li>\r\n    <li>  	Honest, diligent and committed to the department.</li>\r\n  <li> 	Matured</li>\r\n    <li> 	Able to relate and work harmoniously with superiors and co-workers.</li>\r\n    <li> 	With or without experience</li>\r\n</ul>', 'sgh', '2017-03-15', 'open', 'non'),
(172, 'Medical Records Staff', '\r\n<ul>\r\n    <li> 	A Graduate of any four year course</li>]\r\n    <li> 	Not more than 30 years old</li>\r\n    <li> 	Preferably male</li>\r\n    <li> 	Must have thorough knowledge of medical terminologies</li>\r\n    <li> 	Proficient in operating transcription machines/typewriter/computer</li>\r\n    <li> 	Works with accuracy and neatness</li>\r\n    <li> 	Uses initiative and independent judgment in her/his work</li>\r\n    <li> 	Honest, responsible and hardworking</li>\r\n    <li> 	Good time management skills and Physically fit</li>\r\n</ul>', 'sgh', '2016-09-21', 'open', 'non'),
